    private void OperadorIgualdadeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_OperadorIgualdadeActionPerformed
        valor2 = Double.parseDouble(Resultado.getText());
        if (operacao == "soma"){
            Resultado.setText(String.valueOf(valor1 + valor2));}
        else if (operacao == "subtracao"){
            Resultado.setText(String.valueOf(valor1 - valor2));
        }

        else if (operacao == "multiplicacao"){
            Resultado.setText(String.valueOf(valor1 * valor2));
        }
        else if (operacao == "divisao"){
            Resultado.setText(String.valueOf(valor1/valor2));

        }
        else if (operacao == "exponencial"){
            Resultado.setText(String.valueOf(Math.pow(valor1, valor2)));
        }
        else if (operacao == "raizquadrada"){
            valor2=0.5;
            Resultado.setText(String.valueOf(Math.pow(valor1, valor2)));}
        else if (operacao == "porcentagem"){
            Resultado.setText(String.valueOf((valor1/valor2)*100 + "%"));
        }
        
            //}

        //}

    }//GEN-LAST:event_OperadorIgualdadeActionPerformed

